import axios from "axios";

const API_URL = "https://3f628628a342.ngrok-free.app"; // backend root URL

const formApi = axios.create({ 
  baseURL: API_URL,
  headers: {
    'ngrok-skip-browser-warning': 'true'
  }
});

// Add token interceptor
formApi.interceptors.request.use(config => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Form methods
formApi.getForms = () => formApi.get("/forms");
formApi.getFormById = (id) => formApi.get(`/forms/${id}`);
formApi.createForm = (data) => formApi.post("/forms", data);
formApi.updateForm = (id, data) => formApi.put(`/forms/${id}`, data);

// Reference data methods
formApi.getRegions = () => formApi.get("/reference/regions");
formApi.getAreas = (regionId) => formApi.get(`/reference/areas/${regionId}`);
formApi.getInstitutes = (areaId) => formApi.get(`/reference/institutes/${areaId}`);
formApi.getProfessions = (instituteId, gender) => formApi.get(`/reference/professions/${instituteId}/${gender}`);

export default formApi;
