import axios from "axios";

const API_URL = "http://localhost:5000/forms";

// Create an axios instance
const formApi = axios.create({
  baseURL: API_URL,
});

// Add token to every request if available
formApi.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Define CRUD operations
const formService = {
  getForms: () => formApi.get("/"),             // GET /forms
  getForm: (id) => formApi.get(`/${id}`),       // GET /forms/:id
  createForm: (data) => formApi.post("/", data),// POST /forms
  updateForm: (id, data) => formApi.put(`/${id}`, data), // PUT /forms/:id
  // deleteForm: (id) => formApi.delete(`/${id}`), // DELETE /forms/:id
};

export default formService;
