import axios from "axios";

const API_URL = "http://localhost:5000"; // backend root URL

const adminApi = axios.create({ baseURL: `${API_URL}/admin` });

// Add token interceptor
adminApi.interceptors.request.use(config => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Example helpers
adminApi.register = (data) => adminApi.post("/register", data);
adminApi.login = (data) => adminApi.post("/login", data);
adminApi.logout = () => adminApi.post("/logout"); 
// (your backend logout just returns a message, but this keeps API consistent)

// You can add more if you have additional admin routes
// adminApi.getAllAdmins = () => adminApi.get("/all");

export default adminApi;
